css:
/* RETIRA OPCOES DE PAGAMENTO DA PAGINA DE PRODUTO */
.product__payment.card.mb-3 {
   display: none
}

/* BOTAO COMPRAR SEMPRE VISIVEL NAS VITRINES DO MOBILE */
@media screen and (max-width: 480px) {
.product-card__buy.fade {
  opacity: 1 !important;
}}

/* ALTERA COR E LARGURA DO BOTÃO COMPRAR */
.product__buy button {
color: #ffffff;
background-color: #5f9903;
border-color: #5f9903;
width: 100%;
}
.product__buy button:hover {
background: #4e7d02;
border-color: #4e7d02;
}
.product-card__buy button, .product-card__buy button:hover  {
background-color: #5f9903;
border-color: #5f9903;
  color: #fff;
}

/* RETIRA A QUANTIDADE EM ESTOQUE DA PAGINA DE PRODUTO */
.product__short-stock {
display: none !important;
}

/* RETIRA DATA DE NASCIMENTO DO FORMULARIO DE CADASTRO DE CLIENTE */
#account-form-birth,
label[for="account-form-birth"] {
display: none
}

/* RETIRA OPCAO DE CANCELAR COMPRA NA PAGINA DE CONFIRMACAO DE PEDIDO */
.order-info__toggle {
    display: none !important;
}

/* RETIRA OS TERMOS DE FILTROS UTILIZADOS ACIMA DA VITRINE */
.search-engine__info {
  display: none !important;
}

/* RETIRA CUPOM DE DESCONTO DO CHECKOUT */
.discount-applier { display: none }

/* COR DO TEXTO NA SELEÇÃO DE FRETE */
.list-group-item-action.active .shipping-line > small {
color: #fff !important;
}

/* AJUSTE DE TEXTO DO FRETE NO MOBILE */
.shipping-line>small {
    display: block;
    display: -webkit-inline-box;
    overflow: initial !important;
    -webkit-line-clamp: 4  !important
}

/* REMOVER CATEGORIAS DO MENU */
#c-63594db573321213f8bdde63 {display: none !important}


/* POSICIONA RECOMENDAÇÃO DE PRODUTOS ABAIXO DO FRETE NO CARRINHO */

#cart .row .col-md-7.col-lg-8 {
  display: flex;
  flex-direction: column
}

#cart .row .recommended-items {
  order: 1
}
