css:
/* RETIRA OPCOES DE PAGAMENTO DA PAGINA DE PRODUTO */
.product__payment.card.mb-3 {
   display: none
}


/* BOTAO COMPRAR SEMPRE VISIVEL NAS VITRINES DO MOBILE */
@media screen and (max-width: 480px) {
.products-carousel__item  .product-card__buy.fade {
  opacity: 1 !important;
}
}


/* ALTERA COR E LARGURA DO BOT√ÉO COMPRAR */
.product__buy button {
color: #ffffff;
background-color: #5f9903;
border-color: #5f9903;
width: 100%;
}
.product__buy button:hover {
background: #4e7d02;
border-color: #4e7d02;
}
.product-card__buy button, .product-card__buy button:hover  {
background-color: #5f9903;
border-color: #5f9903;
  color: #fff;
}


/* RETIRA A QUANTIDADE EM ESTOQUE DA PAGINA DE PRODUTO */
.product__short-stock {
display: none !important;
}


/* RETIRA DATA DE NASCIMENTO DO FORMULARIO DE CADASTRO DE CLIENTE */
#account-form-birth,
label[for="account-form-birth"] {
display: none
}

/* RETIRA OPCAO DE CANCELAR COMPRA NA PAGINA DE CONFIRMACAO DE PEDIDO */
.order-info__toggle {
    display: none !important;
}
